frames.customCss:
/* hide the help, home, search, and productivity overview buttons, create extra space, and prevent toast pop-up from acting weird */
[aria-label="Go to Home view"], #quick_find, [aria-label="Productivity"], [aria-label="Help & Feedback"] {
	display: none !important;
}

.view_content {
	padding-left: 15px;
}

.view_header {
	padding-left: 15px;
	padding-top: 10px;
}

.undo_toast {
	width: 95%;
}

/* Apply the dark filter and background to the HTML and specific divs, set text to white */
html, .view-content.custom-frames-view, .workspace-leaf.mod-active {
  -webkit-filter: invert(90%) hue-rotate(180deg) !important;
  filter: invert(90%) hue-rotate(180deg) !important;
  background: rgb(25, 25, 25) !important;
  color: var(--text-white) !important;
}

/* Reset filters for images and other elements you don't want to be affected */
img, video {
  -webkit-filter: invert(100%) hue-rotate(-180deg) !important;
  filter: invert(100%) hue-rotate(-180deg) !important;
}
